var mysql=require("mysql");
var connenction=mysql.createConnection({
    host:"127.0.0.1",
    port:3306,
    database:'stusys',
    user:"root",
    password:"279293"
});

function getConnect(){
    connenction.connect(function (err) {
        if(err){
            console.info("mysql数据库连接失败..");

        }else {
            console.info("mysql数据库连接成功..");
        }
    })
}

connenction.on("error", function (err) {
    if(err.code==="PROTOCOL_CONNECTION_LOST"){
        console.info("与数据库的连接被断开。。");
    }else {
        console.info(err);
        throw err;
    }
})

getConnect();